AREA bss (RAM,REL)



;-----------------------------------------------
;  EQUATES
;-----------------------------------------------
bPOWERMASK:          equ   03h         ; Power field mask for CR3
LPF_CNUMMASK:        equ   1fh         ; Mask for A, B & C Caps
LPF_CFBMASK:         equ   80h         ; Mask for Feedback Caps
LPF_POLARITYMASK:    equ   20h         ; Mask of Asign field for CR0

LPF2_3_IfCond:          equ LPF2_3_AMD_MSK & 0x01

IF (LPF2_3_AMD_VAL)
 IF (LPF2_3_IfCond)
  LPF2_3_AMD:           equ LPF2_3_AMD_VAL
 ELSE
  LPF2_3_AMD:           equ LPF2_3_AMD_VAL << 4
 ENDIF
ENDIF

AREA UserModules (ROM,REL)
;-----------------------------------------------------------------------------
;  FUNCTION NAME: LPF2_3_Start
;
;  DESCRIPTION: Applies power setting to the module's PSoC blocks
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:    A register contains the power setting (constant)
;  RETURNS:      Nothing
;  SIDE EFFECTS: 
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;

 LPF2_3_Start:
_LPF2_3_Start:
 LPF2_3_SetPower:
_LPF2_3_SetPower:
    RAM_PROLOGUE RAM_USE_CLASS_2
    and A, bPOWERMASK                        ; mask A to protect unchanged bits
    mov X, SP                                ; define temp store location

    push A                                   ; put power value in temp store

IF (LPF2_3_AMD_VAL)
   M8C_SetBank1
   mov   A, reg[LPF2_3_AMD_REG]                  ; read modulator register
   and   A, ~LPF2_3_AMD_MSK                      ; clear modulator bittfield in A
   or    A, LPF2_3_AMD                           ; combine modulator value with balance of register
   mov   reg[LPF2_3_AMD_REG], A                  ; move complete value back to register
   M8C_SetBank0
ENDIF

    mov A, reg[LPF2_3_FLIN_CR3]    ; read power value
    and A, ~bPOWERMASK                       ; clear power bits in A
    or  A, [X]                               ; combine power value with balance of reg.
    mov reg[LPF2_3_FLIN_CR3], A    ; move complete value back to register

    mov A, reg[LPF2_3_FLOUT_CR3]   ; read power value
    and A, ~bPOWERMASK                       ; clear power bits in A
    or  A, [X]                               ; combine power value with balance of reg.
    mov reg[LPF2_3_FLOUT_CR3], A   ; move complete value back to register
    pop A
    RAM_EPILOGUE RAM_USE_CLASS_2
    ret

