;-----------------------------------------------------------------------------
;  FUNCTION NAME: LPF2_3_SetCA
;            and  LPF2_3_SetCB
;
;  DESCRIPTION:   Alters the filter transfer function by modifying the value
;                 of the op-amp feedback capacitors (see LPF2_3.inc)
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:    The A register contains one of the LPF2_3_FEEDBACK
;                  constants defined in LPF2_3.inc
;  RETURNS:      Nothing.
;  SIDE EFFECTS: 
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;

 LPF2_3_SetCA:
_LPF2_3_SetCA:
    RAM_PROLOGUE RAM_USE_CLASS_1
    cmp   A, LPF2_3_FEEDBACK_32    ; Change Feedback to 32 units?
    jz    .SetCAto32                         ;    Yes, go make it so
                                             ;     No, clear it to 16 units...
    and   reg[LPF2_3_FILT_CA_REG], ~LPF_CFBMASK
    RAM_EPILOGUE RAM_USE_CLASS_1 
    ret
.SetCAto32:
    or    reg[LPF2_3_FILT_CA_REG],  LPF_CFBMASK
    RAM_EPILOGUE RAM_USE_CLASS_1
    ret


 LPF2_3_SetCB:
_LPF2_3_SetCB:
    RAM_PROLOGUE RAM_USE_CLASS_1
    cmp   A, LPF2_3_FEEDBACK_32    ; Change Feedback to 32 units?
    jz    .SetCAto32                         ;    Yes, go make it so
                                             ;     No, clear it to 16 units...
    and   reg[LPF2_3_FILT_CB_REG], ~LPF_CFBMASK
    RAM_EPILOGUE RAM_USE_CLASS_1
    ret
.SetCAto32:
    or    reg[LPF2_3_FILT_CB_REG],  LPF_CFBMASK
    RAM_EPILOGUE RAM_USE_CLASS_1
    ret

;-----------------------------------------------------------------------------
;  FUNCTION NAME: LPF2_3_SetPolarity
;
;  DESCRIPTION:   Alters the output polarity of the data by altering the
;                 input polarity at FLIN
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:    The A register contains one of the LPF2_3_POLARITY
;                  constants defined in LPF2_3.inc
;  RETURNS:      Nothing.
;  SIDE EFFECTS: 
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;

 LPF2_3_SetPolarity:
_LPF2_3_SetPolarity:
    RAM_PROLOGUE RAM_USE_CLASS_1
    cmp   A, LPF2_3_POLARITY_NON_INVERTING ; Should the output be non-inverting?
    jz    .SetPoltoNonInv                         ;    Yes, go make it so
                                                  ;    No, then set it to Inverting...
    or    reg[LPF2_3_FLIN_CR0], LPF_POLARITYMASK
    RAM_EPILOGUE RAM_USE_CLASS_1
    ret
.SetPoltoNonInv:
    and   reg[LPF2_3_FLIN_CR0], ~LPF_POLARITYMASK
    RAM_EPILOGUE RAM_USE_CLASS_1
    ret

;-----------------------------------------------------------------------------
;  FUNCTION NAME: LPF2_3_Stop
;
;  DESCRIPTION: Cuts power to the user module.
;
;-----------------------------------------------------------------------------
;
;  ARGUMENTS:     None
;  RETURNS:       Nothing
;  SIDE EFFECTS: 
;    The A and X registers may be modified by this or future implementations
;    of this function.  The same is true for all RAM page pointer registers in
;    the Large Memory Model.  When necessary, it is the calling function's
;    responsibility to perserve their values across calls to fastcall16 
;    functions.
;
 LPF2_3_Stop:
_LPF2_3_Stop:

    RAM_PROLOGUE RAM_USE_CLASS_1
    and reg[LPF2_3_FLIN_CR3],  ~bPOWERMASK
    and reg[LPF2_3_FLOUT_CR3], ~bPOWERMASK
    RAM_EPILOGUE RAM_USE_CLASS_1
    ret


